import { useState } from "react";
import styled from "styled-components";
import { login } from "../redux/apiCalls";
import { mobile } from "../responsive";
import { useDispatch, useSelector } from "react-redux";

const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(
      rgba(255, 255, 255, 0.5),
      rgba(255, 255, 255, 0.5)
    ),
    url("https://images.pexels.com/photos/6984650/pexels-photo-6984650.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940")
      center;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Wrapper = styled.div`
  width: 25%;
  padding: 20px;
  background-color: white;
  ${mobile({ width: "75%" })}
`;

const Title = styled.h1`
  font-size: 24px;
  font-weight: 300;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 10px 0;
  padding: 10px;
`;

const Button = styled.button`
  width: 40%;
  border: none;
  padding: 15px 20px;
  background-color: teal;
  color: white;
  cursor: pointer;
  margin-bottom: 10px;
  &:disabled {
    color: green;
    cursor: not-allowed;
  }
`;

const Link = styled.a`
  margin: 5px 0px;
  font-size: 12px;
  text-decoration: underline;
  cursor: pointer;
`;

const Error = styled.span`
  color: red;
`;

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();
  const { isFetching, error } = useSelector((state) => state.user);


  const userLogin = (e) => {
    e.preventDefault();
    const userData = {
      username,
      password,
    };
    axios
      .post("http://localhost:3001/api/auth/login", userData)
      .then((result) => {
        if (result.data.token) {
          localStorage.clear();
          localStorage.setItem("username", result.data.username);

          if (result.data.username === "admin") {
            /// success
            localStorage.setItem("t", result.data.token);
            localStorage.setItem("admin", true);
            // setMessage("login success");
            toast.success("login as admin");
            window.location.replace("/Adminhome");

            // window.location.href="/"
          } else {
            localStorage.setItem("t", result.data.token);
            // invalid
            // setMessage("Invalid Login Please try again!!")
            toast.success("login success as user");
            window.location.replace("/Loginhome");
          }
        } else {
          toast.error("Invalid credentials");
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };

  return (
    <Container>
      <Wrapper>
        <Title>SIGN IN</Title>
        <Form>
          <Input
            placeholder="username"
            onChange={(e) => setUsername(e.target.value)}
          />
          <Input
            placeholder="password"
            type="password"
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button onClick={userLogin} disabled={isFetching}>
            LOGIN
          </Button>
          <Link><button> DO NOT REMEMBER THE PASSWORD?</button></Link>
          <Link to = '/register'><button>CREATE A NEW ACCOUNT</button></Link>
        </Form>
      </Wrapper>
    </Container>
  );
};

export default Login;
